import {useState, useContext, useEffect, useRef} from 'react'
import {QuizContext} from '../Helpers/Contexts'
import {Questions} from '../Helpers/QuestionBank'

export default function Quiz(){
  const { score, setScore, setGameState } = useContext(QuizContext)
  const [currQuestion, setCurrQuestion] = useState(0)
  const imgSrc = Questions[currQuestion].image

  const handleAnswer = (e) => {
    setCurrQuestion(currQuestion + 1);
    const optionChosen = e.target.innerText;
    if(optionChosen === Questions[currQuestion].answer) {
      setScore(score + 1);
    }
    if(currQuestion === Questions.length - 1) finishQuiz();
  }

  const finishQuiz = () => {
    setGameState("result")
  }

  const intervalRef = useRef(null)
  const [timer, setTimer] = useState('00:00:00')

  function getTimeRemaining(endtime){
    const total = Date.parse(endtime) - Date.parse(new Date())
    const seconds = Math.floor( (total/1000) % 60)
    const minutes = Math.floor( (total/1000/60) % 60)
    const hours = Math.floor( (total/1000*60*60)%24)
    const days= Math.floor( total/ (1000*60*60*24))
    return{
      total, days, hours, minutes, seconds
  }
  }

  console.log(getTimeRemaining(new Date()))
  function startTimer(deadline){
    let { total, days, hours, minutes, seconds } = getTimeRemaining(deadline)
    if(total >= 0){
      setTimer(
        (hours > 9 ? hours : '0'+hours) + ':' +
        (minutes > 9 ? minutes : '0'+minutes) + ':' +
        (seconds > 9 ? seconds : '0'+seconds)
      )
    }else{
      clearInterval(intervalRef.current)
    }
  }

  function clearTimer(endtime){
    setTimer('00:00:10')
    if(intervalRef.current) clearInterval(intervalRef.current)
    const id = setInterval(()=>{
      startTimer(endtime)
    }, 1000)
    intervalRef.current = id
  }

  function getDeadlineTime(){
    let deadline = new Date()
    deadline.setSeconds(deadline.getSeconds() + 10)
    return deadline
  }

  useEffect(()=>{
    clearTimer(getDeadlineTime())
    return() => {if(intervalRef.current) clearInterval(intervalRef.current)}
  }, [])

  function onClickResetBtn(){
    if(intervalRef.current) clearInterval(intervalRef.current)
    clearTimer(getDeadlineTime())
  }

  return(
    <div className="Quiz">
      <div className="Timer">{timer}</div>
      <button onClick={onClickResetBtn}>Reset</button>
      <h1 className="QuizText">{Questions[currQuestion].q}</h1>
      <div className="Image">
        <img src={imgSrc}></img>
      </div>
      <div className="Options">
        {Questions[currQuestion].options.map((option) => (
          <button onClick={handleAnswer}>{option}</button>
        ))}
      </div>
    </div>
  )
}
