import {useState, useContext, useEffect, useRef} from 'react';
import {QuizContext} from '../Helpers/Contexts';
import {Questions} from '../Helpers/QuestionBank';

export default function Quiz(){
  const { score, setScore, setGameState } = useContext(QuizContext);
  const [currQuestion, setCurrQuestion] = useState(0);
  const imgSrc = Questions[currQuestion].image;

  const [seconds, setSeconds] = useState(5);
  const indexNum = useRef(currQuestion);


  const handleAnswer = (e) => {
    setCurrQuestion(currQuestion + 1);
    const optionChosen = e.target.innerText;
    if(optionChosen === Questions[currQuestion].answer) {
      setScore(score + 1);
    }
    if(currQuestion === Questions.length - 1) finishQuiz();
  }

  const finishQuiz = () => {
    setGameState("result");
  }

  useEffect(() => {
    const countDown = setInterval( () => {
      setSeconds(seconds - 1)
      if(seconds === 0){
        setSeconds(0)
        clearInterval(countDown)
        console.log("hi")
        setCurrQuestion(currQuestion + 1)
        console.log(currQuestion)
        setSeconds(5)
      }
    }
      // if(seconds === 0){
      //   console.log("zero")
      //   clearInterval(countDown)
      //   setCurrQuestion(currQuestion + 1)
      // }
  , 1000)

    return () => { 
      console.log("over")
      clearInterval(countDown)
    }
  },[seconds, indexNum])
  //   const countDown = setInterval(()=>{
  //     setSeconds(seconds - 1)
  //     if(seconds === 0){
  //       clearInterval(countDown)
  //     }
  //   }, 1000)

  //   return () => {
  //     clearInterval(countDown)
  //     setSeconds(5)
  //     console.log("over")
  //   }
  // })


  return(
    <div className="Quiz">
      <p>{seconds}</p>
      <h1 className="QuizText">{Questions[currQuestion].q}</h1>
      <div className="Image">
        <img src={imgSrc}></img>
      </div>
      <div className="Options">
        {Questions[currQuestion].options.map((option) => (
          <button onClick={handleAnswer}>{option}</button>
        ))}
      </div>
    </div>
  )
}
