import {useState, useContext} from 'react';
import {QuizContext} from '../Helpers/Contexts';
import {Questions} from '../Helpers/QuestionBank';
import Timer from 'Timer';

export default function Quiz(){
  const { score, setScore, setGameState } = useContext(QuizContext);

  const [currQuestion, setCurrQuestion] = useState(0);
  // const [optionChosen, setOptionChosen] = useState("");

  const handleAnswer = () => {
    setCurrQuestion(currQuestion + 1);

    if(currQuestion === Questions.length - 1) finishQuiz();
  }

  const finishQuiz = () => {
    setGameState("result");
  }

  return(
    <div className="Quiz">
      <Timer />
      <h1>{Questions[currQuestion].q}</h1>
      <div className="Options">
        <button onClick={handleAnswer}>{Questions[currQuestion].options[0]}</button>
        <button onClick={handleAnswer}>{Questions[currQuestion].options[1]}</button>
        <button onClick={handleAnswer}>{Questions[currQuestion].options[2]}</button>
        <button onClick={handleAnswer}>{Questions[currQuestion].options[3]}</button>
      </div>
    </div>
  )
}
