export default function Result(){
const { score, setScore, setGameState } = useContext(QuizContext);
const [result, setResult ] = useState("");

const showResult = () => {
  if(score === 1){
    setResult("hi")
  }else if(score === 2){
    setResult("hi2")
  }else if(score === 3){
    setResult("hi3")
  }

}

  return(
    <div className="Result">
    {result}
    </div>
  )
}
