import {useState, useContext, useEffect} from 'react'
import {QuizContext} from '../Helpers/Contexts'


export default function Result(){
const { score } = useContext(QuizContext)
// const [result, setResult] = useState([])

const [result, setResult] = useState({name: '', text: ''})

useEffect(() => {
  if(score < 10) {
    // setResult(["톱백귀", "안녕하세요1"])
    setResult({name: 'hello1', text:'world1'})
  }else if(score > 9 && score < 14) {
    // setResult(["막귀", "안녕하세요2"])
    setResult({name: 'hello2', text:'world2'})
  }else if(score > 13 && score < 18) {
    // setResult(["마귀", "안녕하세요3"])
    setResult({name: 'hello33', text:'world3'})
  }else if(score > 17){
    // setResult(["악귀", "안녕하세요4"])
    setResult({name: 'hello', text:'world'})
  }
}, [score])

  const doCopy = text => {
    if (!document.queryCommandSupported("copy")) {
      return alert("복사하기가 지원되지 않는 브라우저입니다.")
    }

    const textarea = document.createElement("textarea")
    textarea.value = text
    textarea.style.top = 0
    textarea.style.left = 0
    textarea.style.display = "fixed"

    document.body.appendChild(textarea)
    textarea.focus()
    textarea.select()

    document.execCommand("copy")

    document.body.removeChild(textarea)
    alert("클립보드에 복사되었습니다.")
  };

  return(
    <div className="Result">
      <div className="ResultText">
        {/* <h3>{result[0]}</h3>
        <p>{result[1]}</p> */}
        <h3>{result.name}</h3>
        <p>{result.text}</p>
        </div>
      <div className="ImageBox">
        <img src="images/001.jpeg" alt="결과 이미지" />
        </div>

      <button className="RestartBtn" onClick={()=>{window.location.reload()}}>restart</button>
      <button className="CopyBtn" onClick={()=>doCopy(window.location.href)}>copy</button>
    </div>
  )
}
