import {useState, useContext, useEffect} from 'react';
import {QuizContext} from '../Helpers/Contexts';


export default function Result(){
const { score, setScore, setGameState } = useContext(QuizContext);
const [result, setResult ] = useState("");

useEffect(() => {
  if(score === 1) {
    setResult("당신은 막귀입니다. 왜 그러세요.")
  }else if(score === 2) {
    setResult("당신은 악귀")
  }else if(score === 3) {
    setResult("당신은 병신입니다. 힘내세요. 웃음병에 걸렸습니다. 더 이상 어떻게 할 수 없어요. 어쩌고 저쩌고. 하하하.")
  }
})
  return(
    <div className="Result">
      <h1 className="logo">Indie-Test</h1>
      <div className="ImageBox"></div>
      <div className="ResultText">{result}</div>
      <button className="RestartBtn" onClick={()=>{window.location.reload()}}>restart</button>
    </div>
  )
}
