import { useState, useEffect } from "react";

export default function Timer() {
  let [seconds, setSeconds] = useState(5);

  useEffect(() => {
    const countdown = setInterval(() => {
      setSeconds(seconds = seconds - 1)
      if(seconds === 0) {
          console.log("hi")
      }
    }, 1000);
    return () => clearInterval(countdown);
  }, [seconds]);

  return (
    <div>
      <h1></h1>
      <div>
        <h2>
          {`0${seconds}`}
        </h2>
      </div>
    </div>
  );
}
